import { useState } from 'react'
import { useRef } from 'react'
import { useContext } from 'react'
import { SECRET_KEY } from '../../utils/constants/general'
import { authContext } from '../../store/authcontext'
import classes from './AuthForm.module.css'

const AuthForm = () => {
  const ahuthCtx = useContext(authContext)
	const [isLogin, setIsLogin] = useState(true)
	const [isLodaing, setIsLoading] = useState(false)




	const switchAuthModeHandler = () => {
		setIsLogin((prevState) => !prevState)
	}

	const submitHandler = (event) => {
		event.preventDefault()

		const enteredEmail = emailInputRef.current.value
		const enteredPassword = passwordInputRef.current.value

		//Todo add validarion
		setIsLoading(true)
		let url
		if (isLogin) {
			url = `https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=${SECRET_KEY}`
		} else {
			url = `https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=${SECRET_KEY}`
			fetch(url, {
				method: 'POST',
				body: JSON.stringify({
					email: enteredEmail,
					password: enteredPassword,
					returnSecureToken: true, //Нам нужуен token и token кайтар дейбиз
				}),
				headers: {
					'Content-Type': 'application/json',
				},
			})
				.then((response) => {
					setIsLoading(false)
					if (response.ok) {
						return response.json()
					} else {
						response.json().then((data) => {
							let errorMessage = 'Authrntication failed'
							if (data && data.error && data.eror.message) {
								errorMessage = data.error.message
							}
							throw new Error(errorMessage)
						})
					}
				})
				.then((data) => {
					authContext.login(data.idToken)
				})
				.catch((err) => {
					alert(err.message)
				})
		}
	}

	return (
		<section className={classes.auth}>
			<h1>{isLogin ? 'Login' : 'Sign Up'}</h1>
			<form onSubmit={submitHandler}>
				<div className={classes.control}>
					<label htmlFor='email'>Your Email</label>
					<input
						ref={emailInputRef}
						type='email'
						id='email'
						required
					/>
				</div>
				<div className={classes.control}>
					<label htmlFor='password'>Your Password</label>
					<input
						ref={passwordInputRef}
						type='password'
						id='password'
						required
					/>
				</div>
				<div className={classes.actions}>
					{!isLodaing && (
						<button>{isLogin ? 'Login' : 'Create Account'}</button>
					)}
					{isLodaing && <p>Sending request...</p>}
					<button
						type='button'
						className={classes.toggle}
						onClick={switchAuthModeHandler}
					>
						{isLogin
							? 'Create new account'
							: 'Login with existing account'}
					</button>
				</div>
			</form>
		</section>
	)
}

export default AuthForm
