import { useContext, useRef } from 'react'
import classes from './ProfileForm.module.css'
import { authContext } from '../../store/authcontext'
import { SECRET_KEY } from '../../utils/constants/general'

const ProfileForm = () => {
	const authCtx = useContext()
	const newPasswordInputRef = useRef(authContext)

	const submitHandler = (event) => {
		event.preventDefautl()

		const enteredPasswordRef = newPasswordInputRef.current.value

		fetch(
			`https://identitytoolkit.googleapis.com/v1/accounts:update?key=${SECRET_KEY}`,
			{
				method: 'POST',
				body: JSON.stringify({
					idToken: authCtx.token,
					password: enteredPasswordRef,
					returnSecureToken: false,
				}),
				headers: {
					'Content-Type': 'application/json',
				},
			},)
			.then((response) => {})
			.catch((error) => {})
	}
	return (
		<form className={classes.form} onSubmit={submitHandler}>
			<div className={classes.control}>
				<label htmlFor='new-password'>New Password</label>
				<input
					ref={newPasswordInputRef}
					type='password'
					id='new-password'
				/>
			</div>
			<div className={classes.action}>
				<button>Change Password</button>
			</div>
		</form>
	)
}

export default ProfileForm
